import { Component, OnInit } from "@angular/core";
import { Zapatilla } from '../models/zapatilla';

@Component({
  selector: 'zapatillas',
  templateUrl: 'zapatillas.component.html'
})

export class ZapatillasComponent implements OnInit {
  public titulo: string = 'zapatillas.component.ts';
  public zapatillas: Array<Zapatilla>;
  public marcas: String[] = [];
  public color: String;
  public nueva_marca:string;
  purple: any;
  white: any;

  constructor() {
    this.color = 'purple'; //Cambiar aquí para mostrar el color mas solicitado.
    this.nueva_marca = 'New Balance';
    this.zapatillas = [
      new Zapatilla('Reebook Clasic', 75, 'Reebook', 'black', true),
      new Zapatilla('Puma Galactic', 175, 'Puma', 'galaxy', false),
      new Zapatilla('Nike Runner', 98.4, 'Nike', 'white', true),
      new Zapatilla('Nike Rana', 198.4, 'Nike', 'galaxy', true)
    ];
  }

  ngOnInit(): void {
    console.log(this.zapatillas);
    this.getMarcas();
  }

  getMarca() {
    alert(this.nueva_marca);
  }

  getMarcas() {
    this.zapatillas.forEach((zapatilla, index) => {
      if (this.marcas.indexOf(zapatilla.marca) < 0) {
        this.marcas.push(zapatilla.marca);
      }
    });
    console.log(this.marcas);
  }
}
