import { Component } from '@angular/core';
import { Configuracion } from './models/configuracion';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public title = 'mi-angular';
  public descripcion: string;
  config; //Se puede omitir el public


  constructor() {
    this.config = Configuracion;
    this.title = Configuracion.titulo;
    this.descripcion = Configuracion.descripcion;
  }
}
<!DOCTYPE html>
<html lang="es">
  <head>
    <meta charset="UTF-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>{{ title }}</title>
  </head>
  <body>
    <div
      [ngStyle]="{
        background: config.fondo,
        color: config.color,
        border: '3px dashed white',
        padding: '20px'
      }"
    >
      <h1>{{ title }}</h1>
      <h3>{{ descripcion }}</h3>
      <hr />
      <br />
      <nav>
        <a [routerLink]="['/home']" [routerLinkActive]="['active']">Home</a
        >&nbsp;
        <a [routerLink]="['/cursos']" [routerLinkActive]="['active']">Cursos</a
        >&nbsp;
        <a [routerLink]="['/externo']" [routerLinkActive]="['active']"
          >Externo</a
        >&nbsp;
        <a [routerLink]="['/videojuegos']" [routerLinkActive]="['active']"
          >Videojuegos</a
        >&nbsp;
        <a [routerLink]="['/zapatillas']" [routerLinkActive]="['active']"
          >Zapatillas</a
        >&nbsp;
        <a [routerLink]="['/contacto']" [routerLinkActive]="['active']"
          >Conta</a
        >
      </nav>
    </div>

    <h3>Esta es la página app.component.html</h3>

    <hr />
    <!--Aquí se cargan todas las rutas de nuestros componentes-->
    <h2>router-outlet</h2>
    <router-outlet></router-outlet>
  </body>
</html>
