import { Component } from '@angular/core';
import { ContactoUsuario } from '../models/contactousuario';

@Component({
  selector: 'app-contacto',
  templateUrl: './contacto.component.html',
  styleUrls: ['./contacto.component.css']
})
export class ContactoComponent {
  public contactoUsuario: ContactoUsuario;
  public show_data

  constructor(){
    this.contactoUsuario = new ContactoUsuario('','','','');
  }

  onSubmit(form: { reset: () => void; }) {
    this.show.data
    form.reset();
    //console.log('submit hecho');
    console.log(this.contactoUsuario);
  }
}
<h1>Página de contacto</h1>
<hr>
<form #formContacto="ngForm" (ngSubmit)="onSubmit(formContacto)">
  <label for="nombre">Nombre</label><br>
  <input type="text" name="nombre" #nombre="ngModel" [(ngModel)]="contactoUsuario.nombre" required pattern="[a-zA-Z]+">
  <span *ngIf="nombre.touched && !nombre.valid">
    El nombre es obligatorio!
  </span><br>

  <label for="apellidos">Apellidos</label><br>
  <input type="text" name="apellidos" #apellidos="ngModel" [(ngModel)]="contactoUsuario.apellidos" required pattern="[a-zA-Z]+">
  <span *ngIf="apellidos.touched && !apellidos.valid">
    Los apellidos son obligatorios!
  </span><br>

  <label for="email">Email</label><br>
  <input type="email" name="email" #email="ngModel" [(ngModel)]="contactoUsuario.email" required>
  <span *ngIf="apellidos.touched && !apellidos.valid">
    El email es obligatorio!
  </span><br>

  <label for="mensaje">Mensaje</label><br>
  <textarea name="mensaje" id="" cols="30" rows="10" #mensaje="ngModel" [(ngModel)]="contactoUsuario.mensaje" required></textarea><br>
  <span *ngIf="apellidos.touched && !apellidos.valid">
    el mensaje es obligatorio!
  </span><br>
  <input type="submit" value="Enviar" [disabled]="!formContacto.form.valid">
</form>
