import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  public identificado: boolean = false;

  setIdentificdo() {
    this.identificado = true
  }

  unSetIdentificad0() {
    this.identificado = false;
  }
}
<h2>ESTA ES LA PÁGINA => home works!</h2>

<div *ngIf="identificado; then siIdentificado else noIdentificado "></div>

<ng-template #siIdentificado>
  <h3>Estás identificado en la aplicación</h3>
  <button (click)="unSetIdentificado()">Borrar identificación</button>
</ng-template>

<ng-template #noIdentificado>
  <h3>No estás identificado en la aplicación</h3>
</ng-template>
