import { Component, OnInit } from "@angular/core";
import { Zapatilla } from '../models/zapatilla';

@Component({
    selector: 'zapatillas',
    templateUrl: 'zapatillas.component.html'
})

export class ZapatillasComponent implements OnInit {
    public titulo: string = 'zapatillas.component.ts';
    public zapatillas: Array<Zapatilla>;
    public marcas: String[] = [];
    public color: String;
purple: any;

    constructor() {
        this.color = 'purple';
        this.zapatillas = [
            new Zapatilla('Reebook Clasic', 75, 'Reebook', 'black', true),
            new Zapatilla('Puma Galactic', 175, 'Puma', 'galaxy', false),
            new Zapatilla('Nike Runner', 98.4, 'Nike', 'white', true)
        ];
    }

    ngOnInit(): void {
        console.log(this.zapatillas);
        this.getMarcas();
    }

    getMarcas() {
        this.zapatillas.forEach((zapatilla, index) => {
            if(this.marcas.indexOf(zapatilla.marca) < 0) {
            this.marcas.push(zapatilla.marca);
            }
        });
        console.log(this.marcas);
    }
}
<h2>{{titulo}}</h2>
<h3>Tenemos las mejores marcas de zapatillas</h3>
<ul style="list-style-type:none;">
    <li *ngFor="let marca of marcas; let i = index">{{(i+1) + '.- ' + marca}}</li>
</ul>

<div>
    <h3>Las zapatillas que tenemos disponibles son las siguientes:</h3>
    <ul style="list-style-type:none;">
        <li *ngFor="let zapatilla of zapatillas">
            <span [ngStyle]="{'text-decoration':!zapatilla.existencia?'line-through':'none'}">
                Nombre: {{zapatilla.nombre}},
                <span> color: {{zapatilla.color}},</span>
                <strong> precio: € {{zapatilla.precio}}. </strong>
            </span>

            <span *ngIf="zapatilla.precio < 100" [style.background]="zapatilla.precio < 100 ?'green':'blue'"
                [style.color]="zapatilla.precio < 100 ?'yellow':'white'"><i><b> >>>EN OFERTA!!!</b></i></span>
        </li>
    </ul>
</div>

<div>
  <h3>El color que más se solicita es el: </h3>
  <ul [ngSwitch]="color">
    <li *ngSwitchCase="'purple'">El color predominante es el <span> purple</span></li>
    <li *ngSwitchCase="'galaxy'">El color predominante es el <span> galaxy</span></li>
    <li *ngSwitchCase="'black'">El color predominante es el <span> black</span></li>
    <li>El color predominante es el <span> white</span></li>
  </ul>
</div>
