import { Component } from '@angular/core';
import { PeticionesService } from '../services/peticiones.service';

@Component({
  selector: 'app-externo',
  templateUrl: './externo.component.html',
  styleUrls: [
    './externo.component.css',
  ],
  providers: [PeticionesService]
})

export class ExternoComponent {
  public user: any;
  public userId: number;
  public fecha: any;
  public new_user:

  constructor(private _peticionesService: PeticionesService) {
    this.userId = 1;
  }

  ngOnInit() {
    this.peticionUsuario();
    this.fecha = new Date;
  }

  peticionUsuario() {
    this._peticionesService.getUser(this.userId).subscribe(
      result => {
        this.user = result.data;
        //console.log(result);
      },
      error => {
        console.log(<any>error);
      }
    );
  }
}
<h1>externo.component.html</h1>
<hr>
<strong>{{fecha}}</strong>
<hr>
<hr>
<strong>{{fecha | date:'dd/MM/yyyy'}}</strong>
<hr>
<hr>
<strong>{{fecha | date:'fullDate'}}</strong>
<hr>
<hr>
<strong>{{4 | calculadora:5}}</strong>
<hr>
<div>
  <input type="text" [(ngModel)]="userId" (keyup.enter)="peticionUsuario()">
  <div *ngIf="!user">cargando...  :(</div>
</div>
<div>
  <div *ngIf="user">
    <img src="{{user.avatar}}" alt="{{user.first_name + ' ' + user.last_name}}" width="170px">
    <h2>{{user.first_name + ' ' + user.last_name}}</h2>
  </div>
</div>
