import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute, Params } from '@angular/router';

@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.css']
})
export class CursosComponent implements OnInit{
  public titulo: string;
  public nombre: string ='';

  constructor(
    private _route: ActivatedRoute,
    private _router: Router
  ) {
    this.titulo = 'PÁGINA => cursos.component.ts';
  }

  ngOnInit() {
    this._route.params.subscribe((params: Params) => {
      this.nombre = params['nombre'];
      console.log(this.nombre)
    });
  }

  redirigir() {
    this._router.navigate(['/zapatillas']);
  }
}
<div>
    <!-- Este título es el de la clase de cursos.component.ts-->
    <h2>{{titulo}}</h2>
    <h2 *ngIf="nombre">Bienvenido <i>"{{nombre}}"</i></h2>
    <div>
      <button (click)="redirigir()">this._router.navigate(['/zapatillas'])</button>
    </div>
    <div>
      <h3>Esta página cursos.component.ts</h3>
      <ul>
        <li>JavaScript</li>
        <li>TypeScript</li>
        <li>EcmaScript</li>
      </ul>
    </div>
</div>
