import { Component } from '@angular/core';
import { Configuracion } from './models/configuracion';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public title = 'mi-angular';
  public descripcion: string;
  config; //Se puede omitir el public
  public mostrar_lista_juegos: boolean = true;

  constructor() {
    this.config = Configuracion;
    this.title = Configuracion.titulo;
    this.descripcion = Configuracion.descripcion;
  }

  ocultarMostrarLista(value: boolean): void {
      this.mostrar_lista_juegos = value;
  }
}
<!DOCTYPE html>
<html lang="es">

<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>{{title}}</title>
</head>

<body>
  <div [ngStyle]="{
    'background':config.fondo,
    'color':config.color,
    'border':'3px dashed white',
    'padding':'20px'
    }">
    <h1>{{title}}</h1>
    <h3>{{descripcion}}</h3>
  </div>

  <button (click)="ocultarMostrarLista(true)" *ngIf="!mostrar_lista_juegos">Mostrar</button>
  <button (click)="ocultarMostrarLista(false)" *ngIf="mostrar_lista_juegos">Ocultar</button>
  <!-- videojuego.component.ts  @Component({    selector: 'videojuego',-->
  <videojuego *ngIf="mostrar_lista_juegos"></videojuego>

  <!-- zapatillas.component.ts  @Component({ selector: 'zapatillas',-->
  <!---->
  <zapatillas></zapatillas>
  <app-cursos></app-cursos>
</body>

</html>
